INSERT INTO db_film.t_movie (movie_id, name, poster, director, actor, movie_long, language, intro, type, public_date, wish_num, score) VALUES (37, '蜘蛛侠：英雄远征', '/images/movie/1563173216299.jpg', '大奔', '蜘蛛', '180分钟', '英语', '故事全面延续“复联4”，蜘蛛侠志承钢铁侠远征欧洲，独挑大梁对抗群敌！新角色“神秘客”穿越多元宇宙霸气登场！神盾局局长尼克·弗瑞回归领军！“漫威新铁三角”组合强势出击！全新蜘蛛战衣酷炫升级！史诗对决燃爆今夏突破想象极限！', '科幻', '2019-7-11', 0, null);
