create table t_movie
(
    movie_id    int(4) unsigned auto_increment comment '电影id'
        primary key,
    name        varchar(50)               null comment '电影名称',
    poster      varchar(100)              null comment '电影海报',
    director    varchar(20)               null comment '导演',
    actor       varchar(100)              null comment '主演',
    movie_long  varchar(20)               null comment '电影片长',
    language    varchar(50)               null comment '电影语言版本',
    intro       text                      null comment '电影简介',
    type        varchar(50)               null comment '电影类型',
    public_date varchar(20)               null comment '电影上映时间',
    wish_num    int(4) unsigned default 0 null comment '想看人数',
    score       decimal(4, 1)             null comment '电影综合评分'
)
    collate = utf8_unicode_ci;

